//RED BOXES:
.container-1 {
    grid-template-columns: repeat(4, 100px) 150px repeat(4, 100px);
    grid-template-rows: (100px 100px);
    display: grid;
    gap: 1rem;
    $base-color: red;
    $base-letter: cyan;

    .box-darken-4 {
        background-color: darken($color: $base-color, $amount: 20%);
        color:darken($color: $base-letter, $amount: 20%);
        border-radius: 5px;
    }
    .box-darken-3 {
        background-color: darken($color: $base-color, $amount: 15%);
        color:darken($color: $base-letter, $amount: 15%)
    }
    .box-darken-2 {
        background-color: darken($color: $base-color, $amount: 10%);
        color:darken($color: $base-letter, $amount: 10%)
    }
    .box-darken-1 {
        background-color: darken($color: $base-color, $amount: 5%);
        color:darken($color: $base-letter, $amount: 5%)
    }.box-base-color {
        background-color: $base-color;
        color:white;
    }
    .box-lighten-1 {
        background-color: lighten($color: $base-color, $amount: 10%);
        color:lighten($color: $base-letter, $amount: 10%)
    }
    .box-lighten-2 {
        background-color: lighten($color: $base-color, $amount: 20%);
        color:lighten($color: $base-letter, $amount: 20%)
    }
    .box-lighten-3 {
        background-color: lighten($color: $base-color, $amount: 30%);
        color:lighten($color: $base-letter, $amount: 30%)
    }
    .box-lighten-4 {
        background-color: lighten($color: $base-color, $amount: 40%);
        color:lighten($color: $base-letter, $amount: 40%)
    }
}

