$base-color-container-1: red;
$base-color-container-2: blue;
$base-color-container-3: green;

$base-letter-container: white;
$base-letter-container_1: cyan;
$base-letter-container-2: yellow;
$base-letter-container-3: mediumorchid;


.container-1 {
    display: grid;
    grid-template-columns: repeat(4, 100px) 150px repeat(4, 100px);
    grid-template-rows: (100px 100px);
    gap: 1rem;
}

.container-2 {
    display: grid;
    grid-template-columns: repeat(4, 100px) 150px repeat(4, 100px);
    grid-template-rows: (100px 100px);
    gap: 1rem;
}

.container-3 {
    display: grid;
    grid-template-columns: repeat(4, 100px) 150px repeat(4, 100px);
    grid-template-rows: (100px 100px);
    gap: 1rem;
    
}

//letter color:

//RED BOXES:
.container-1 .box-darken-4 {
    background-color: darken($color: $base-color-container-1, $amount: 20%);
    color:darken($color: $base-letter-container-1, $amount: 20%);
    border-radius: 5px;
    place-content:center, center;
}
.container-1 .box-darken-3 {
